-- Problem Statement 5: 
-- Calculate Revenue by Category
-- Design a stored procedure that calculates the total revenue generated by each film category. 
-- The procedure should output a report showing the revenue for each category and the percentage of total revenue it represents.
use sakila
show tables

select * from category
select * from film_category
select * from film
select * from inventory
select * from rental
select * from payment

DELIMITER //
CREATE PROCEDURE Calculate_revenue_by_category()
BEGIN
select name, sum(amount) as Revenue, (sum(amount)*100)/(select sum(amount) from payment) as Percentage from 
category join film_category on category.category_id = film_category.category_id join film on
film_category.film_id = film.film_id join inventory on
film.film_id = inventory.film_id join rental on
inventory.inventory_id = rental.inventory_id join payment on
rental.rental_id = payment.rental_id group by name;
END
//
DELIMITER ;

call Calculate_revenue_by_category();